    public DescriptionResponse getDescriptionItemResponse1688(
            long itemId
    ) throws IOException, InterruptedException {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("http://api.tmapi.top/1688/item_desc?apiToken=" + tmapiToken + "&item_id=" + itemId))
                .method("GET", HttpRequest.BodyPublishers.noBody())
                .build();
        HttpResponse<String> response = HttpClient.newHttpClient().send(request, HttpResponse.BodyHandlers.ofString());
        String responseBody = response.body();
        tmapiDao.insertSearchDescriptionHistory1688(itemId, responseBody);
        ObjectMapper mapper = new ObjectMapper();
        return mapper.readValue(responseBody, DescriptionResponse.class);
    }

