    public List<Integer> convertKeywordItemCategoryPathIntoCategoryPathIdxList(List<CategoryPath> categoryPathList, List<String> keywordItemCategoryPathList) {
        return keywordItemCategoryPathList.stream()
                .flatMap(keywordCategoryPath -> categoryPathList.stream()
                        .filter(categoryPath -> categoryPath.getCategory_path().equals(keywordCategoryPath))
                        .map(CategoryPath::getIdx))
                .sorted()
                .collect(Collectors.toList());
    }

    public void insertKeywordItemInformation(List<KeywordItem> keywordItemList, String keyword) {
        keywordItemList.forEach(keywordItem -> {
            Long itemId = keywordItem.getItem_id();
            KeywordShopInfo shopInfo = keywordItem.getShop_info();
            KeywordShopScoreInfo shopScoreInfo = shopInfo.getSore_info();
            String shopMemberId = shopInfo.getMember_id();
            // todo shopInfo insert 여부 파악 추가 scoreInfo 는 shopInfo 여부에 따라서 insert 여부 함께 결정
            // shopInfoStream에서 shopMemberId와 일치하는 첫 번째 KeywordShopInfo 객체를 찾음
            Optional<KeywordShopInfo> matchingShopInfo = tmapiDao.getKeywordItemShopInfoStream1688()
                    .filter(i -> i.getMember_id().equals(shopMemberId))
                    .findFirst();

            Integer shopIdx;
            if (matchingShopInfo.isPresent()) {
                // 일치하는 member_id를 가진 KeywordShopInfo 객체가 존재하는 경우
                // shopInfo.idx에 해당 객체의 idx 값을 설정
                shopIdx = matchingShopInfo.get().getIdx();
                shopInfo.setIdx(shopIdx);
            } else {
                // 일치하는 member_id를 가진 KeywordShopInfo 객체가 존재하지 않는 경우
                // shopInfo와 shopScoreInfo를 데이터베이스에 삽입
                shopIdx = insertKeywordItemShopInfo1688(itemId, shopInfo);
                insertKeywordItemScoreInfo1688(shopIdx, shopScoreInfo);
            }
            keywordItem.setShop_idx(shopIdx);

            // todo add shopIdx column and remove itemId from shopInfo table
            // todo category path unique
            List<String> categoryPathStringList = keywordItem.getCategory_path();
            List<String> existingCategoryPathList = tmapiDao.getKeywordItemCategoryPathList1688();
            List<String> newCategoryPathList = categoryPathStringList.stream()
                    .filter(path -> !existingCategoryPathList.contains(path))
                    .toList();

            // 새로운 category_path만 데이터베이스에 삽입
            if (!newCategoryPathList.isEmpty()) {
                insertKeywordItemCategoryPaths1688(newCategoryPathList);
            }

            List<CategoryPath> categoryPathList = getKeywordItemCategoryPathList1688();
            List<Integer> categoryPathIdxList = convertKeywordItemCategoryPathIntoCategoryPathIdxList(
                    categoryPathList, categoryPathStringList);
            keywordItem.setCategory_path_idxs(commonUtil.convertListToString(categoryPathIdxList));
            keywordItem.setSale_info_idx(insertKeywordItemSaleInfo1688(keywordItem.getSale_info()));
            insertKeywordItem1688(keywordItem, keyword);

            insertKeywordItemDeliveryInfo1688(itemId, keywordItem.getDelivery_info());
            insertKeywordItemPriceInfo1688(itemId, keywordItem.getPrice_info());
            insertKeywordItemQuantityPrices1688(itemId, keywordItem.getQuantity_prices());
        });
    }


